 }        echo '</tr>';    $date = date ("Y-m-d", strtotime("+1 day", strtotime($date)));	}  echo '</table>';  // Get banners that fall in the date range  $banners = mysqli_query($db,"    Select campaignid,campaignname,status,activate_time,expire_time,views    from rv_campaigns C    where status = 0    Order By activate_time  ");?></div><?php require_once('footer.php'); ?></body></html>